mapper.select:
SELECT b.id, b.title, b.inserted, COUNT(r.id) numOfReply FROM Board b LEFT JOIN Reply r ON b.id = r.board_id
WHERE title LIKE concat('%',#{keyword},'%')
GROUP BY b.id ORDER BY b.id DESC LIMIT #{from}, #{row}

mapper.update:
	UPDATE Board
  	SET title = #{title}, body = #{body}
  	WHERE id = #{id}
